import React, { useState } from 'react';
import { createReview } from '../api/api';

function Review() {
  const [review, setReview] = useState({ serviceId: '', comment: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createReview(review);
      alert('Review submitted');
    } catch {
      alert('Review failed');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="Service ID" onChange={e => setReview({ ...review, serviceId: e.target.value })} />
      <textarea placeholder="Comment" onChange={e => setReview({ ...review, comment: e.target.value })} />
      <button type="submit">Submit Review</button>
    </form>
  );
}

export default Review;
