import React, { useState } from 'react';
import { createReview } from '../api/api';

function ReviewForm() {
  const [review, setReview] = useState({
    booking_id: '',
    rating: '',
    comment: '',
  });

  const [loading, setLoading] = useState(false);

  // Validate MongoDB ObjectId (24-character hex string)
  const isValidObjectId = (id) => /^[a-f\d]{24}$/i.test(id);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate booking_id
    if (!isValidObjectId(review.booking_id)) {
      alert('Booking ID must be a 24-character valid ObjectId');
      return;
    }

    // Validate rating
    const ratingValue = Number(review.rating);
    if (isNaN(ratingValue) || ratingValue < 1 || ratingValue > 5) {
      alert('Rating must be a number between 1 and 5');
      return;
    }

    try {
      setLoading(true);
      await createReview({
        booking_id: review.booking_id,
        rating: ratingValue,
        comment: review.comment,
      });
      alert('✅ Review submitted successfully!');
      setReview({ booking_id: '', rating: '', comment: '' }); // Reset form
    } catch (error) {
      console.error('Review submission failed:', error);
      const message = error.response?.data?.error || 'Failed to submit review';
      alert(`❌ ${message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 500, margin: 'auto' }}>
      <h2>Submit a Review</h2>

      <label>
        Booking ID:
        <input
          type="text"
          placeholder="24-char MongoDB ObjectId"
          value={review.booking_id}
          onChange={(e) => setReview({ ...review, booking_id: e.target.value })}
          required
        />
      </label>

      <label>
        Rating (1-5):
        <input
          type="number"
          value={review.rating}
          onChange={(e) => setReview({ ...review, rating: e.target.value })}
          min={1}
          max={5}
          required
        />
      </label>

      <label>
        Comment:
        <textarea
          placeholder="Write your review here (optional)"
          value={review.comment}
          onChange={(e) => setReview({ ...review, comment: e.target.value })}
        />
      </label>

      <button type="submit" disabled={loading}>
        {loading ? 'Submitting...' : 'Submit Review'}
      </button>
    </form>
  );
}

export default ReviewForm;
