import React, { useState } from 'react';
import { createReview } from '../api/api';

function ReviewForm() {
  const [review, setReview] = useState({
    booking_id: '',
    rating: '',
    comment: '',
  });

  // Helper to validate ObjectId format (24 hex characters)
  const isValidObjectId = (id) => /^[a-f\d]{24}$/i.test(id);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isValidObjectId(review.booking_id)) {
      alert('Please enter a valid Booking ID (24 hex characters)');
      return;
    }

    if (review.rating < 1 || review.rating > 5) {
      alert('Rating must be between 1 and 5');
      return;
    }

    try {
      await createReview({
        booking_id: review.booking_id,
        rating: Number(review.rating),
        comment: review.comment,
      });
      alert('Review submitted!');
      setReview({ booking_id: '', rating: '', comment: '' });
    } catch (error) {
      console.error(error);
      const message = error.response?.data?.error || error.message || 'Failed to submit review';
      alert(message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        placeholder="Booking ID (24-character ObjectId)"
        value={review.booking_id}
        onChange={(e) => setReview({ ...review, booking_id: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Rating (1-5)"
        value={review.rating}
        onChange={(e) => setReview({ ...review, rating: e.target.value })}
        min={1}
        max={5}
        required
      />
      <textarea
        placeholder="Write your comment (optional)"
        value={review.comment}
        onChange={(e) => setReview({ ...review, comment: e.target.value })}
      />
      <button type="submit">Submit Review</button>
    </form>
  );
}

export default ReviewForm;
