import React, { useState } from 'react';
import { createReview } from '../api/api';

function ReviewForm() {
  const [review, setReview] = useState({
    booking_id: '',  // <- changed from user/serviceId
    comment: '',
    rating: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createReview(review);
      alert(' Review submitted!');
      setReview({ booking_id: '', comment: '', rating: '' }); // reset form
    } catch (error) {
      console.error(error);
      const message = error.response?.data?.error || error.message || 'Failed to submit review';
      alert(message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        placeholder="Booking ID"
        value={review.booking_id}
        onChange={(e) => setReview({ ...review, booking_id: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Rating (1-5)"
        value={review.rating}
        onChange={(e) => setReview({ ...review, rating: Number(e.target.value) })}
        required
      />
      <textarea
        placeholder="Write your comment"
        value={review.comment}
        onChange={(e) => setReview({ ...review, comment: e.target.value })}
      />
      <button type="submit">Submit Review</button>
    </form>
  );
}

export default ReviewForm;
